import React from 'react';
import ReactDOM from 'react-dom';

import Head from './components/Head';
import MenuBar from './components/MenuBar';
import VideoPlayer from './components/VideoPlayer';
import History from './components/History';
import TextInput from './components/TextInput';
import Register from './components/Register';
import Login from './components/Login';

import videoLink from './resources/videos/test.m4v';
import './css/index.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showRegister: false,
      loggedIn: false,
      username: '',
    };
  }

  handleLogin = async (username, password) => {
    const response = await fetch('/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });
    const data = await response.json();
    if (data.success) {
      this.setState({
        loggedIn: true,
        username: username,
      });
    } else {
      alert(data.message);
    }
  };

  render() {
    const { showRegister, loggedIn, username } = this.state;

    if (loggedIn) {
      return <Login handleLogin={this.handleLogin} />;
    }

    return (
      <>
        <Head />
        <MenuBar toggleRegister={this.toggleRegister} username={username} />
        <VideoPlayer videoLink={videoLink} />
        <History />
        <TextInput />
        {showRegister ? <Register toggleRegister={this.toggleRegister} /> : null}
      </>
    );
  }
}

const root = document.getElementById('root');
ReactDOM.render(<App />, root);
