import React, {useState} from 'react';

import Head from './components/Head';
import MenuBar from './components/MenuBar';
import VideoPlayer from './components/VideoPlayer';
import TryHistory from './components/TryHistory';
import VideoHistory from './components/VideoHistory';
import TextInput from './components/TextInput';
import Login from './components/Login';
import Source from './components/Source';

import './css/index.css';

export default function App() {

  const [loggedIn, setLoggedIn] = useState(false);
  const [showVideoHistory, setShowVideoHistory] = useState(false);
  const [username, setUsername] = useState('');
  const [videoLink, setVideoLink] = useState('');
  const [videoSource, setVideoSource] = useState('');

  function toggleVideoHistory() {
    this.setState((prevState) => ({
      showVideoHistory: !prevState.showVideoHistory,
    }));
  };

  function logout() {
    this.setState({
      loggedIn: false,
      showVideoHistory: false
    });
  }

  function handleRegister (username, password) {

    if(username === null || password === null || username === '' || password === '')
    {
      return ;
    }

    const data = {
      username: username,
      password: password
    };

    fetch('https://yousub-api.nassing.tk/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (response.ok) {
        response.text().then(text => {
          if(text === "0") {
            this.setState({
              loggedIn: true,
              username: username
            });        
          }
          else if(text === "1")
          {
            //Error
          }
        })
      } else {
        throw new Error('Error adding element');
      }
    })
    .catch(error => console.log(error.message));
  }

  function getNewVideo() {
    if (username === null || username === '') {
      return;
    }

    const data = {
      username: username
    };

    fetch('https://yousub-api.nassing.tk/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error getting video');
        }
      })
      .then(data => {
        videoLink.setState(data.link)
        sourceLink.setState(data.source);
      })
      .catch(error => console.log(error.message));
  };

  function handleLogin(username, password) {

    if(username === null || password === null || username === '' || password === '')
    {
      return ;
    }

    const data = {
      username: username,
      password: password
    };

    fetch('https://yousub-api.nassing.tk/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (response.ok) {
        response.text().then(text => {
          console.log(text);
          if(text === "0") {
            this.setState({
              loggedIn: true,
              username: username,
            });        
          }
          else if(text === "1")
          {
            //Error
          }
        })
      } else {
        throw new Error('Error adding element');
      }
    })
    .catch(error => console.log(error.message));
  }  

  if (!loggedIn) {
    return <Login handleLogin={handleLogin} handleRegister={handleRegister} />;
  }

  return (
    <>
      <Head />
      <MenuBar toggleVideoHistory={toggleVideoHistory} logout={this.logout}/>
      <VideoPlayer getNewVideo={getNewVideo} />
      <TryHistory videoLink={videoLink}/>
      <TextInput videoLink={videoLink}/>
      <Source sourceLink={videoSource}/>
      {showVideoHistory ? <VideoHistory toggleVideoHistory={toggleVideoHistory}/> : null}
    </>
  );
}
