import React, { useState, useEffect } from 'react';
import '../css/VideoPlayer.css';

const VideoPlayer = (props) => {
  const [videoLink, setVideoLink] = useState('');
  const [videoSource, setVideoSource] = useState('');

  useEffect(() => {
    const { username, setVideoLinkParent } = props;
    getNewVideo(username, setVideoLinkParent);
  }, [props.username]);

  const getNewVideo = (username, setVideoLinkParent) => {
    if (username === null || username === '') {
      return;
    }

    const data = {
      username: username
    };

    fetch('https://yousub-api.nassing.tk/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error getting video');
        }
      })
      .then(data => {
        setVideoLink(require(`../resources/videos/${data.link}`));
        setVideoSource(data.source);
        setVideoLinkParent(data.link);
      })
      .catch(error => console.log(error.message));
  };

  return (
    <div id="video-player-wrapper">
      <div id="video-player">
        <video src={videoLink} controls loop />
      </div>
    </div>
  );
};

export default VideoPlayer;
