import React from 'react';
import ReactDOM from 'react-dom';

import Head from './components/Head';
import MenuBar from './components/MenuBar';
import VideoPlayer from './components/VideoPlayer';
import TryHistory from './components/TryHistory';
import VideoHistory from './components/VideoHistory';
import TextInput from './components/TextInput';
import Login from './components/Login';
import Source from './components/Source';

import './css/index.css';

export default function App() {

  const [loggedIn, setLoggedIn] = React.useState(false);
  const [showVideoHistory, setShowVideoHistory] = React.useState(false);
  const [username, setUsername] = React.useState('');
  const [videoLink, setVideoLink] = React.useState('');
  const [videoSource, setVideoSource] = React.useState('');

  toggleVideoHistory = () => {
    this.setState((prevState) => ({
      showVideoHistory: !prevState.showVideoHistory,
    }));
  };

  logout = () => {
    this.setState({
      loggedIn: false,
      showVideoHistory: false
    });
  }

  handleRegister = (username, password) => {

    if(username === null || password === null || username === '' || password === '')
    {
      return ;
    }

    const data = {
      username: username,
      password: password
    };

    fetch('https://yousub-api.nassing.tk/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (response.ok) {
        response.text().then(text => {
          if(text === "0") {
            this.setState({
              loggedIn: true,
              username: username
            });        
          }
          else if(text === "1")
          {
            //Error
          }
        })
      } else {
        throw new Error('Error adding element');
      }
    })
    .catch(error => console.log(error.message));
  }

  setVideoLinkAndSourceInParent = (vidLink, vidSource) => {
    this.setState({
      videoLink: vidLink,
      videoSource: vidSource
    });
  }

  handleLogin = (username, password) => {

    if(username === null || password === null || username === '' || password === '')
    {
      return ;
    }

    const data = {
      username: username,
      password: password
    };

    fetch('https://yousub-api.nassing.tk/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (response.ok) {
        response.text().then(text => {
          console.log(text);
          if(text === "0") {
            this.setState({
              loggedIn: true,
              username: username,
            });        
          }
          else if(text === "1")
          {
            //Error
          }
        })
      } else {
        throw new Error('Error adding element');
      }
    })
    .catch(error => console.log(error.message));
  }  

  if (!loggedIn) {
    return <Login handleLogin={this.handleLogin} handleRegister={this.handleRegister} />;
  }

  return (
    <>
      <Head />
      <MenuBar toggleVideoHistory={this.toggleVideoHistory} logout={this.logout}/>
      <VideoPlayer username={username} setVideoLinkAndSourceInParent={this.setVideoLinkAndSourceInParent} />
      <TryHistory username={username} videoLink={videoLink}/>
      <TextInput username={username} videoLink={videoLink}/>
      <Source sourceLink={videoSource}/>
      {showVideoHistory ? <VideoHistory toggleVideoHistory={this.toggleVideoHistory}/> : null}
    </>
  );
}
