import '../css/History.css';
import React from 'react';

export default class History extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      correctHistory: ['This is america', 'Another item', 'One more item', 'Last item'],
      incorrectHistory: ['America', 'Another item', 'One more item', 'Last item'],
      currentIndex: 0,
    };
  }    

  handleLeft = () => {
    this.setState((prevState) => ({
      currentIndex: prevState.currentIndex === 0 ? prevState.history.length - 1 : prevState.currentIndex - 1,
    }));
  };

  handleRight = () => {
    this.setState((prevState) => ({
      currentIndex: prevState.currentIndex === prevState.history.length - 1 ? 0 : prevState.currentIndex + 1,
    }));
  };

  render() {
    const { history, currentIndex } = this.state;
    const currentItem = history[currentIndex];
    return (
      <div id="history-wrapper">
        <div id="history">
          <div id="history-main">
            <p onClick={this.handleLeft}>{'<'}</p>
            <p id="history-item-correct">{currentItem}</p>
            <p id="history-item-incorrect">{currentItem}</p>
            <p onClick={this.handleRight}>{'>'}</p>
          </div>
          <div id="history-pages-number">
            <p>{`${currentIndex + 1}/${history.length}`}</p>
          </div>
        </div>
      </div>
    );
  }
}
