import '../css/TextInput.css';
import React, {useState} from 'react';

export default function TextInput({username, videoLink}) {
  const [state] = useState({
        userInput: ''
  });

  function handleInput(event) {
    this.setState({userInput: event.target.value});
  }

  function handleSubmit() {
    const { userInput } = state;
    if(username === null || videoLink === null || username === '' || videoLink === '')
    {
      return ;
    }

    const data = {
      username: username,
      videoLink: videoLink,
      userInput: userInput
    };

    fetch('https://yousub-api.nassing.tk/input', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (response.ok) {
        response.text().then(text => {
          if(text === "0") {
            this.setState({
              userInput: ''
            });        
          }
          else if(text === "1")
          {
            //Error
          }
        })
      } else {
        throw new Error('Error adding element');
      }
    })
    .catch(error => console.log(error.message)); 
  }    

  return(
  <div id="text-input-wrapper">
    <textarea
      placeholder="Enter your text here..."
      value={state.userInput}
      onChange={handleInput}
      autocomplete="off"
      autofocus
      rows="3"
    /> 
    <button onClick={handleSubmit()}>Submit</button>
  </div>);
}
