import React from 'react';
import ReactDOM from 'react-dom';

import Head from './components/Head';
import MenuBar from './components/MenuBar';
import VideoPlayer from './components/VideoPlayer';
import TryHistory from './components/TryHistory';
import VideoHistory from './components/VideoHistory';
import TextInput from './components/TextInput';
import Login from './components/Login';
import Source from './components/Source';

import videoLink from './resources/videos/test.m4v';
import './css/index.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loggedIn: false,
      showVideoHistory: false,
    };
  }

  toggleVideoHistory = () => {
    this.setState((prevState) => ({
      showVideoHistory: !prevState.showVideoHistory,
    }));
  };

  handleRegister = (username, password) => {
    const data = {
      username: username,
      password: password
    };

    fetch('https://yousub-api.com/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (response.ok) {
        response.text().then(text => {
          if(text === "0") {
            this.setState({
              loggedIn: true,
            });        
          }
          else if(text === "1")
          {
            //ToDo
          }
        })
      } else {
        throw new Error('Error adding element');
      }
    })
    .catch(error => console.log(error));
  }

  handleLogin = (username, password) => {
    const data = {
      username: username,
      password: password
    };

    fetch('http://localhost:5000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (response.ok) {
        response.text().then(text => {
          if(text === "0") {
            this.setState({
              loggedIn: true,
            });        
          }
          else if(text === "1")
          {
            //ToDo
          }
        })
      } else {
        throw new Error('Error adding element');
      }
    })
    .catch(error => console.log(error));
  }


  render() {
    const { loggedIn, username, showVideoHistory } = this.state;

    if (!loggedIn) {
      return <Login handleLogin={this.handleLogin} handleRegister={this.handleRegister} />;
    }

    return (
      <>
        <Head />
        <MenuBar username={username} toggleVideoHistory={this.toggleVideoHistory}/>
        <VideoPlayer videoLink={videoLink} />
        <TryHistory />
        <TextInput />
        <Source sourceLink="https://youtube.com"/>
        {showVideoHistory ? <VideoHistory toggleVideoHistory={this.toggleVideoHistory}/> : null}
      </>
    );
  }
}

const root = document.getElementById('root');
ReactDOM.render(<App />, root);
